
import { useState, useEffect } from "react";
import { Skeleton } from "@/components/ui/skeleton";

interface OptimizedImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  className?: string;
  fallbackSrc?: string;
  quality?: number;
  priority?: boolean;
}

export const OptimizedImage = ({
  src,
  alt,
  width,
  height,
  className = "",
  fallbackSrc,
  quality = 60,
  priority = false
}: OptimizedImageProps) => {
  const [loading, setLoading] = useState(true);
  const [imageSrc, setImageSrc] = useState<string>("");
  const [error, setError] = useState(false);
  
  // Tiny, low quality placeholder
  const tinyPlaceholder = "data:image/jpeg;base64,";

  useEffect(() => {
    // Set initial state
    setLoading(true);
    setError(false);
    
    // For priority images, don't show the placeholder to avoid flashing
    setImageSrc(priority ? src : tinyPlaceholder);
    
    // Optimize the image URL if needed
    let optimizedSrc = src;
    
    // Create image object to preload
    const img = new Image();
    img.onload = () => {
      setImageSrc(optimizedSrc);
      setLoading(false);
    };
    img.onerror = () => {
      setError(true);
      setLoading(false);
      if (fallbackSrc) {
        setImageSrc(fallbackSrc);
      }
    };
    img.src = optimizedSrc;
    
    // Set a maximum time to wait for image loading
    const timer = setTimeout(() => {
      if (loading) {
        setLoading(false);
      }
    }, 800);
    
    return () => clearTimeout(timer);
  }, [src, fallbackSrc, width, priority]);
  
  if (loading && !priority) {
    return <Skeleton className={`${className} bg-gray-100`} style={{ width, height }} />;
  }
  
  return (
    <img 
      src={imageSrc}
      alt={alt}
      width={width}
      height={height}
      className={className}
      loading={priority ? "eager" : "lazy"}
      decoding="async"
      fetchPriority={priority ? "high" : "auto"}
      onError={() => {
        if (fallbackSrc && !error) {
          setImageSrc(fallbackSrc);
          setError(true);
        }
      }}
    />
  );
};

export default OptimizedImage;
